import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

// In local/offline scenarios (e.g., downloaded from Replit) allow the app to boot
// without a DATABASE_URL by exporting a lax fallback. Any actual DB call will
// throw a clear error, but consumers can choose an in-memory storage instead.
const databaseUrl = process.env.DATABASE_URL;

let pool: Pool | undefined = undefined;
let db: any = undefined;

if (databaseUrl) {
  pool = new Pool({ connectionString: databaseUrl });
  db = drizzle({ client: pool, schema });
} else {
  // Soft fallback for dev: export a proxy that throws on use
  const throwing = new Proxy({}, {
    get() {
      throw new Error("Database is not configured (DATABASE_URL missing).");
    }
  });
  db = throwing as any;
}

export { pool, db };

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  const host = process.env.HOST || process.env.BIND_HOST || (process.platform === 'win32' ? '127.0.0.1' : '0.0.0.0');
  const listenOpts: any = { port, host };
  if (process.platform !== 'win32') {
    listenOpts.reusePort = true;
  }
  server.listen(listenOpts, () => {
    log(`serving on http://${host}:${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import multer from "multer";
import path from "path";
import fs from "fs";
import { storage } from "./storage";
import { insertClaimSchema, insertFileSchema } from "@shared/schema";
import { ocrService } from "./services/ocrService";

// Configure multer for file uploads
const uploadDir = path.join(process.cwd(), "uploads");
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}

const upload = multer({
  dest: uploadDir,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['application/pdf', 'image/jpeg', 'image/png', 'image/jpg'];
    if (allowedTypes.includes(file.mimetype)) {
      cb(null, true);
    } else {
      cb(new Error('Invalid file type. Only PDF, JPG, and PNG files are allowed.'));
    }
  },
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Dashboard stats endpoint
  app.get("/api/dashboard/stats", async (req, res) => {
    try {
      const stats = await storage.getDashboardStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // File upload endpoint
  app.post("/api/upload", upload.single("file"), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "No file uploaded" });
      }

      // Create file record
      const fileData = {
        filename: req.file.filename,
        originalName: req.file.originalname,
        mimetype: req.file.mimetype,
        size: req.file.size,
        status: "uploaded" as const,
        claimId: null, // Will be linked after OCR processing
        userId: null, // Add user authentication later
      };

      const insertFileData = insertFileSchema.parse(fileData);
      const uploadedFile = await storage.createFile(insertFileData);

      // Process file with OCR
      await storage.updateFileStatus(uploadedFile.id, "processing");

      try {
        const ocrResult = await ocrService.processFile(req.file.path, req.file.mimetype);
        await storage.updateFileStatus(uploadedFile.id, "processed");

        res.json({
          file: uploadedFile,
          ocrResult,
        });
      } catch (ocrError) {
        console.error("OCR processing failed:", ocrError);
        await storage.updateFileStatus(uploadedFile.id, "failed");
        res.status(500).json({ message: "OCR processing failed", file: uploadedFile });
      }
    } catch (error) {
      console.error("Upload error:", error);
      res.status(500).json({ message: "Upload failed" });
    }
  });

  // Claims CRUD endpoints
  app.get("/api/claims", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : undefined;
      const claims = await storage.getClaims(limit);
      res.json(claims);
    } catch (error) {
      console.error("Error fetching claims:", error);
      res.status(500).json({ message: "Failed to fetch claims" });
    }
  });

  app.get("/api/claims/:id", async (req, res) => {
    try {
      const claim = await storage.getClaim(req.params.id);
      if (!claim) {
        return res.status(404).json({ message: "Claim not found" });
      }
      res.json(claim);
    } catch (error) {
      console.error("Error fetching claim:", error);
      res.status(500).json({ message: "Failed to fetch claim" });
    }
  });

  app.post("/api/claims", async (req, res) => {
    try {
      const claimData = insertClaimSchema.parse(req.body);
      const claim = await storage.createClaim(claimData);
      res.status(201).json(claim);
    } catch (error) {
      console.error("Error creating claim:", error);
      res.status(400).json({ message: "Failed to create claim" });
    }
  });

  app.patch("/api/claims/:id", async (req, res) => {
    try {
      const updates = insertClaimSchema.partial().parse(req.body);
      const claim = await storage.updateClaim(req.params.id, updates);
      res.json(claim);
    } catch (error) {
      console.error("Error updating claim:", error);
      res.status(400).json({ message: "Failed to update claim" });
    }
  });

  app.delete("/api/claims/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteClaim(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Claim not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting claim:", error);
      res.status(500).json({ message: "Failed to delete claim" });
    }
  });

  // Map data endpoint - returns GeoJSON
  app.get("/api/map/claims", async (req, res) => {
    try {
      const claims = await storage.getClaims();

      // Filter claims that have boundary geometry
      const claimsWithGeometry = claims.filter(claim => claim.boundaryGeometry);

      // Convert to GeoJSON FeatureCollection
      const geojson = {
        type: "FeatureCollection",
        features: claimsWithGeometry.map(claim => ({
          type: "Feature",
          properties: {
            id: claim.id,
            claimId: claim.claimId,
            claimantName: claim.claimantName,
            village: claim.village,
            district: claim.district,
            state: claim.state,
            area: claim.area,
            status: claim.status,
            ocrConfidence: claim.ocrConfidence,
          },
          geometry: claim.boundaryGeometry,
        })),
      };

      res.json(geojson);
    } catch (error) {
      console.error("Error fetching map data:", error);
      res.status(500).json({ message: "Failed to fetch map data" });
    }
  });

  // Save polygon boundary for a claim
  app.post("/api/claims/:id/boundary", async (req, res) => {
    try {
      const { geometry } = req.body;

      if (!geometry) {
        return res.status(400).json({ message: "Geometry is required" });
      }

      const claim = await storage.updateClaim(req.params.id, {
        boundaryGeometry: geometry,
      });

      res.json(claim);
    } catch (error) {
      console.error("Error saving boundary:", error);
      res.status(400).json({ message: "Failed to save boundary" });
    }
  });

  // Process OCR corrections and create claim
  app.post("/api/ocr/save", async (req, res) => {
    try {
      const ocrData = req.body;

      // Validate required fields
      if (!ocrData.claimantName || !ocrData.village || !ocrData.claimId || !ocrData.area) {
        return res.status(400).json({ message: "Missing required claim data" });
      }

      // Check if claim ID already exists
      const existingClaim = await storage.getClaimByClaimId(ocrData.claimId);
      if (existingClaim) {
        return res.status(400).json({ message: "Claim ID already exists" });
      }

      const claimData = {
        claimId: ocrData.claimId,
        claimantName: ocrData.claimantName,
        village: ocrData.village,
        district: ocrData.district || null,
        state: ocrData.state || null,
        area: ocrData.area,
        surveyNumber: ocrData.surveyNumber || null,
        status: "pending" as const,
        ocrConfidence: ocrData.confidence || null,
        boundaryGeometry: null,
        rawOcrText: ocrData.rawText || null,
        userId: null, // Add user authentication later
      };

      const claim = await storage.createClaim(claimData);

      // Link file to claim if fileId provided
      if (ocrData.fileId) {
        // Update the uploaded file to link it to this claim
        await storage.updateFileStatus(ocrData.fileId, "processed");
      }

      res.status(201).json(claim);
    } catch (error) {
      console.error("Error saving OCR data:", error);
      res.status(400).json({ message: "Failed to save claim data" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { claims, uploadedFiles, users, type User, type InsertUser, type Claim, type InsertClaim, type UploadedFile, type InsertFile, type ClaimWithFiles, type DashboardStats } from "@shared/schema";
import { db } from "./db";
import { eq, desc, count, sum } from "drizzle-orm";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Claims
  getClaim(id: string): Promise<ClaimWithFiles | undefined>;
  getClaimByClaimId(claimId: string): Promise<Claim | undefined>;
  getClaims(limit?: number): Promise<ClaimWithFiles[]>;
  createClaim(claim: InsertClaim): Promise<Claim>;
  updateClaim(id: string, updates: Partial<InsertClaim>): Promise<Claim>;
  deleteClaim(id: string): Promise<boolean>;

  // Files
  getFile(id: string): Promise<UploadedFile | undefined>;
  getFilesByClaimId(claimId: string): Promise<UploadedFile[]>;
  createFile(file: InsertFile): Promise<UploadedFile>;
  updateFileStatus(id: string, status: "uploaded" | "processing" | "processed" | "failed"): Promise<UploadedFile>;

  // Dashboard
  getDashboardStats(): Promise<DashboardStats>;
}

export class DatabaseStorage implements IStorage {
  // Users
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  // Claims
  async getClaim(id: string): Promise<ClaimWithFiles | undefined> {
    const [claim] = await db.select().from(claims).where(eq(claims.id, id));
    if (!claim) return undefined;

    const files = await db.select().from(uploadedFiles).where(eq(uploadedFiles.claimId, id));
    return { ...claim, files };
  }

  async getClaimByClaimId(claimId: string): Promise<Claim | undefined> {
    const [claim] = await db.select().from(claims).where(eq(claims.claimId, claimId));
    return claim || undefined;
  }

  async getClaims(limit = 50): Promise<ClaimWithFiles[]> {
    const claimsList = await db
      .select()
      .from(claims)
      .orderBy(desc(claims.createdAt))
      .limit(limit);

    const claimsWithFiles = await Promise.all(
      claimsList.map(async (claim: Claim) => {
        const files = await db
          .select()
          .from(uploadedFiles)
          .where(eq(uploadedFiles.claimId, claim.id));
        return { ...claim, files };
      })
    );

    return claimsWithFiles;
  }

  async createClaim(insertClaim: InsertClaim): Promise<Claim> {
    const [claim] = await db
      .insert(claims)
      .values({
        ...insertClaim,
        updatedAt: new Date(),
      })
      .returning();
    return claim;
  }

  async updateClaim(id: string, updates: Partial<InsertClaim>): Promise<Claim> {
    const [claim] = await db
      .update(claims)
      .set({
        ...updates,
        updatedAt: new Date(),
      })
      .where(eq(claims.id, id))
      .returning();
    return claim;
  }

  async deleteClaim(id: string): Promise<boolean> {
    const result = await db.delete(claims).where(eq(claims.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  // Files
  async getFile(id: string): Promise<UploadedFile | undefined> {
    const [file] = await db.select().from(uploadedFiles).where(eq(uploadedFiles.id, id));
    return file || undefined;
  }

  async getFilesByClaimId(claimId: string): Promise<UploadedFile[]> {
    return await db.select().from(uploadedFiles).where(eq(uploadedFiles.claimId, claimId));
  }

  async createFile(insertFile: InsertFile): Promise<UploadedFile> {
    const [file] = await db
      .insert(uploadedFiles)
      .values(insertFile)
      .returning();
    return file;
  }

  async updateFileStatus(id: string, status: "uploaded" | "processing" | "processed" | "failed"): Promise<UploadedFile> {
    const [file] = await db
      .update(uploadedFiles)
      .set({ status })
      .where(eq(uploadedFiles.id, id))
      .returning();
    return file;
  }

  // Dashboard
  async getDashboardStats(): Promise<DashboardStats> {
    const [totalClaimsResult] = await db.select({ count: count() }).from(claims);
    const [processedResult] = await db
      .select({ count: count() })
      .from(claims)
      .where(eq(claims.status, "approved"));
    const [totalAreaResult] = await db
      .select({ total: sum(claims.area) })
      .from(claims);
    const [pendingResult] = await db
      .select({ count: count() })
      .from(claims)
      .where(eq(claims.status, "pending"));

    return {
      totalClaims: totalClaimsResult.count,
      processed: processedResult.count,
      totalArea: Number(totalAreaResult.total) || 0,
      pending: pendingResult.count,
    };
  }
}

// In-memory fallback storage for environments without a configured database
class MemoryStorage implements IStorage {
  private users: User[] = [];
  private claimsData: ClaimWithFiles[] = [];
  private filesData: UploadedFile[] = [];

  async getUser(id: string): Promise<User | undefined> {
    return this.users.find(u => u.id === id);
  }
  async getUserByUsername(username: string): Promise<User | undefined> {
    return this.users.find(u => u.username === username);
  }
  async createUser(user: InsertUser): Promise<User> {
    const created: User = { id: crypto.randomUUID(), createdAt: new Date(), ...user } as any;
    this.users.push(created);
    return created;
  }

  async getClaim(id: string): Promise<ClaimWithFiles | undefined> {
    return this.claimsData.find(c => c.id === id);
  }
  async getClaimByClaimId(claimId: string): Promise<Claim | undefined> {
    return this.claimsData.find(c => c.claimId === claimId) as any;
  }
  async getClaims(limit = 50): Promise<ClaimWithFiles[]> {
    return this.claimsData.slice(0, limit);
  }
  async createClaim(claim: InsertClaim): Promise<Claim> {
    const created: any = {
      id: crypto.randomUUID(),
      createdAt: new Date(),
      updatedAt: new Date(),
      status: 'pending',
      ...claim,
    };
    this.claimsData.unshift({ ...created, files: [] });
    return created;
  }
  async updateClaim(id: string, updates: Partial<InsertClaim>): Promise<Claim> {
    const idx = this.claimsData.findIndex(c => c.id === id);
    if (idx === -1) throw new Error('Claim not found');
    const current = this.claimsData[idx];
    const updated: any = { ...current, ...updates, updatedAt: new Date() };
    this.claimsData[idx] = { ...updated, files: current.files };
    return updated;
  }
  async deleteClaim(id: string): Promise<boolean> {
    const before = this.claimsData.length;
    this.claimsData = this.claimsData.filter(c => c.id !== id);
    this.filesData = this.filesData.filter(f => f.claimId !== id);
    return this.claimsData.length < before;
  }

  async getFile(id: string): Promise<UploadedFile | undefined> {
    return this.filesData.find(f => f.id === id);
  }
  async getFilesByClaimId(claimId: string): Promise<UploadedFile[]> {
    return this.filesData.filter(f => f.claimId === claimId);
  }
  async createFile(file: InsertFile): Promise<UploadedFile> {
    const created: UploadedFile = { id: crypto.randomUUID(), createdAt: new Date(), ...file } as any;
    this.filesData.push(created);
    return created;
  }
  async updateFileStatus(id: string, status: "uploaded" | "processing" | "processed" | "failed"): Promise<UploadedFile> {
    const file = this.filesData.find(f => f.id === id);
    if (!file) throw new Error('File not found');
    (file as any).status = status;
    return file;
  }

  async getDashboardStats(): Promise<DashboardStats> {
    const totalClaims = this.claimsData.length as any;
    const processed = this.claimsData.filter(c => c.status === 'approved').length as any;
    const totalArea = this.claimsData.reduce((acc, c) => acc + (Number(c.area) || 0), 0);
    const pending = this.claimsData.filter(c => c.status === 'pending').length as any;
    return { totalClaims, processed, totalArea, pending } as any;
  }
}

const usingDb = !!process.env.DATABASE_URL;
export const storage: IStorage = usingDb ? new DatabaseStorage() : new MemoryStorage();

