import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/hooks/useFileUpload.ts.content:
import { useState, useCallback } from "react";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import type { OCRResult, FileUploadState } from "@/types";

export function useFileUpload() {
  const [uploadState, setUploadState] = useState<FileUploadState>({
    isUploading: false,
    uploadProgress: 0,
    error: null,
  });

  const [ocrResult, setOCRResult] = useState<{ file: any; ocrResult: OCRResult } | null>(null);
  const { toast } = useToast();

  const uploadFile = useCallback(async (file: File) => {
    setUploadState({
      isUploading: true,
      uploadProgress: 0,
      error: null,
    });

    try {
      // Simulate upload progress
      const progressInterval = setInterval(() => {
        setUploadState(prev => ({
          ...prev,
          uploadProgress: Math.min(prev.uploadProgress + 10, 90),
        }));
      }, 200);

      const result = await api.uploadFile(file);

      clearInterval(progressInterval);

      setUploadState({
        isUploading: false,
        uploadProgress: 100,
        error: null,
      });

      setOCRResult(result);

      toast({
        title: "Upload Successful",
        description: `OCR processing completed with ${result.ocrResult.confidence}% confidence`,
      });

      return result;
    } catch (error) {
      setUploadState({
        isUploading: false,
        uploadProgress: 0,
        error: error instanceof Error ? error.message : "Upload failed",
      });

      toast({
        title: "Upload Failed",
        description: error instanceof Error ? error.message : "Unknown error occurred",
        variant: "destructive",
      });

      throw error;
    }
  }, [toast]);

  const resetUpload = useCallback(() => {
    setUploadState({
      isUploading: false,
      uploadProgress: 0,
      error: null,
    });
    setOCRResult(null);
  }, []);

  return {
    uploadState,
    ocrResult,
    uploadFile,
    resetUpload,
  };
}

client/src/lib/api.ts.content:
import { apiRequest } from "@/lib/queryClient";
import type { 
  DashboardStats, 
  Claim, 
  ClaimWithFiles, 
  OCRResult, 
  InsertClaim 
} from "@shared/schema";

export const api = {
  // Dashboard
  getDashboardStats: async (): Promise<DashboardStats> => {
    const response = await apiRequest("GET", "/api/dashboard/stats");
    return response.json();
  },

  // File upload
  uploadFile: async (file: File): Promise<{ file: any; ocrResult: OCRResult }> => {
    const formData = new FormData();
    formData.append("file", file);

    const response = await fetch("/api/upload", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.message || "Upload failed");
    }

    return response.json();
  },

  // Claims
  getClaims: async (limit?: number): Promise<ClaimWithFiles[]> => {
    const url = limit ? `/api/claims?limit=${limit}` : "/api/claims";
    const response = await apiRequest("GET", url);
    return response.json();
  },

  getClaim: async (id: string): Promise<ClaimWithFiles> => {
    const response = await apiRequest("GET", `/api/claims/${id}`);
    return response.json();
  },

  createClaim: async (claim: InsertClaim): Promise<Claim> => {
    const response = await apiRequest("POST", "/api/claims", claim);
    return response.json();
  },

  updateClaim: async (id: string, updates: Partial<InsertClaim>): Promise<Claim> => {
    const response = await apiRequest("PATCH", `/api/claims/${id}`, updates);
    return response.json();
  },

  deleteClaim: async (id: string): Promise<void> => {
    await apiRequest("DELETE", `/api/claims/${id}`);
  },

  // Map data
  getMapClaims: async (): Promise<any> => {
    const response = await apiRequest("GET", "/api/map/claims");
    return response.json();
  },

  saveClaimBoundary: async (claimId: string, geometry: any): Promise<Claim> => {
    const response = await apiRequest("POST", `/api/claims/${claimId}/boundary`, { geometry });
    return response.json();
  },

  // OCR
  saveOCRData: async (ocrData: any): Promise<Claim> => {
    const response = await apiRequest("POST", "/api/ocr/save", ocrData);
    return response.json();
  },
};

