import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { api } from "@/lib/api";
import { FileText, CheckCircle, Ruler, Clock } from "lucide-react";

export function StatsGrid() {
  const { data: stats, isLoading } = useQuery({
    queryKey: ["/api/dashboard/stats"],
    queryFn: api.getDashboardStats,
  });

  if (isLoading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {[...Array(4)].map((_, i) => (
          <Card key={i}>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div className="space-y-2">
                  <Skeleton className="h-4 w-20" />
                  <Skeleton className="h-8 w-16" />
                </div>
                <Skeleton className="h-12 w-12 rounded-lg" />
              </div>
              <Skeleton className="h-4 w-24 mt-4" />
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (!stats) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <Card>
          <CardContent className="p-6 text-center">
            <p className="text-muted-foreground">Failed to load statistics</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  const statsConfig = [
    {
      title: "Total Claims",
      value: stats.totalClaims.toLocaleString(),
      change: "+12.5%",
      changeLabel: "from last month",
      icon: FileText,
      iconColor: "text-primary",
      bgColor: "bg-primary/10",
    },
    {
      title: "Processed",
      value: stats.processed.toLocaleString(),
      change: "+8.2%",
      changeLabel: "completion rate",
      icon: CheckCircle,
      iconColor: "text-accent",
      bgColor: "bg-accent/10",
    },
    {
      title: "Total Area",
      value: stats.totalArea.toLocaleString(),
      change: "",
      changeLabel: "hectares mapped",
      icon: Ruler,
      iconColor: "text-chart-2",
      bgColor: "bg-chart-2/10",
    },
    {
      title: "Pending Review",
      value: stats.pending.toLocaleString(),
      change: "-5.8%",
      changeLabel: "from last week",
      icon: Clock,
      iconColor: "text-chart-1",
      bgColor: "bg-chart-1/10",
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
      {statsConfig.map((stat, index) => {
        const Icon = stat.icon;
        return (
          <Card key={index} className="shadow-sm">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">
                    {stat.title}
                  </p>
                  <p 
                    className="text-3xl font-bold text-foreground"
                    data-testid={`stat-${stat.title.toLowerCase().replace(" ", "-")}`}
                  >
                    {stat.value}
                  </p>
                </div>
                <div className={`w-12 h-12 ${stat.bgColor} rounded-lg flex items-center justify-center`}>
                  <Icon className={`h-6 w-6 ${stat.iconColor}`} />
                </div>
              </div>
              <div className="flex items-center mt-4 text-sm">
                {stat.change && (
                  <span 
                    className={`font-medium ${
                      stat.change.startsWith("+") ? "text-accent" : "text-chart-1"
                    }`}
                  >
                    {stat.change}
                  </span>
                )}
                <span className={`text-muted-foreground ${stat.change ? "ml-1" : ""}`}>
                  {stat.changeLabel}
                </span>
              </div>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );
}

client/src/components/FileUpload/FileUpl.content:
import { useCallback, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { useFileUpload } from "@/hooks/useFileUpload";
import { OCRReviewModal } from "./OCRReviewModal";
import { CloudUpload, FolderOpen, FileText, Image, CheckCircle, Clock } from "lucide-react";
import { cn } from "@/lib/utils";

export function FileUpload() {
  const [isDragOver, setIsDragOver] = useState(false);
  const [showOCRModal, setShowOCRModal] = useState(false);
  const { uploadState, ocrResult, uploadFile, resetUpload } = useFileUpload();

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  }, []);

  const handleDrop = useCallback(async (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);

    const files = Array.from(e.dataTransfer.files);
    const file = files[0];

    if (file) {
      const result = await uploadFile(file);
      if (result) {
        setShowOCRModal(true);
      }
    }
  }, [uploadFile]);

  const handleFileSelect = useCallback(async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const result = await uploadFile(file);
      if (result) {
        setShowOCRModal(true);
      }
    }
  }, [uploadFile]);

  const handleOCRModalClose = useCallback(() => {
    setShowOCRModal(false);
    resetUpload();
  }, [resetUpload]);

  return (
    <div className="space-y-8">
      <Card className="shadow-sm">
        <CardHeader>
          <CardTitle className="text-lg font-semibold text-foreground">
            Document Upload
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div
            className={cn(
              "border-2 border-dashed rounded-lg p-8 text-center transition-colors",
              isDragOver 
                ? "border-primary bg-primary/5" 
                : "border-border hover:border-primary hover:bg-primary/5",
              uploadState.isUploading && "pointer-events-none opacity-50"
            )}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            data-testid="file-drop-zone"
          >
            <CloudUpload className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <h4 className="text-lg font-medium text-foreground mb-2">
              Drop files here
            </h4>
            <p className="text-sm text-muted-foreground mb-4">
              or click to browse
            </p>

            <input
              type="file"
              accept=".pdf,.jpg,.jpeg,.png"
              onChange={handleFileSelect}
              className="hidden"
              id="file-upload"
              disabled={uploadState.isUploading}
            />

            <Button
              asChild
              disabled={uploadState.isUploading}
              data-testid="button-select-files"
            >
              <label htmlFor="file-upload" className="cursor-pointer">
                <FolderOpen className="mr-2 h-4 w-4" />
                Select Files
              </label>
            </Button>

            <p className="text-xs text-muted-foreground mt-4">
              Supports PDF, JPG, PNG up to 10MB
            </p>

            {uploadState.isUploading && (
              <div className="mt-6">
                <Progress value={uploadState.uploadProgress} className="mb-2" />
                <p className="text-sm text-muted-foreground">
                  Processing... {uploadState.uploadProgress}%
                </p>
              </div>
            )}

            {uploadState.error && (
              <div className="mt-4 p-3 bg-destructive/10 text-destructive rounded-md text-sm">
                {uploadState.error}
              </div>
            )}
          </div>

          {/* Recent Uploads */}
          <div className="mt-6">
            <h4 className="text-sm font-medium text-foreground mb-3">
              Recent Uploads
            </h4>
            <div className="space-y-2">
              {/* Sample recent uploads - in real app this would come from API */}
              <div className="flex items-center justify-between p-3 bg-muted rounded-md">
                <div className="flex items-center">
                  <FileText className="h-5 w-5 text-destructive mr-3" />
                  <div>
                    <p className="text-sm font-medium text-foreground">
                      claim_doc_001.pdf
                    </p>
                    <p className="text-xs text-muted-foreground">
                      2.3 MB • Processed
                    </p>
                  </div>
                </div>
                <span className="text-xs text-accent font-medium flex items-center">
                  <CheckCircle className="h-3 w-3 mr-1" />
                  OCR Complete
                </span>
              </div>

              <div className="flex items-center justify-between p-3 bg-muted rounded-md">
                <div className="flex items-center">
                  <Image className="h-5 w-5 text-chart-1 mr-3" />
                  <div>
                    <p className="text-sm font-medium text-foreground">
                      survey_map_042.jpg
                    </p>
                    <p className="text-xs text-muted-foreground">
                      5.1 MB • Processing
                    </p>
                  </div>
                </div>
                <span className="text-xs text-chart-1 font-medium flex items-center">
                  <Clock className="h-3 w-3 mr-1" />
                  In Queue
                </span>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      <OCRReviewModal
        isOpen={showOCRModal}
        onClose={handleOCRModalClose}
        ocrData={ocrResult}
      />
    </div>
  );
}

client/src/components/FileUpload/OCRRevi.content:
import { useState, useCallback, useEffect } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { api } from "@/lib/api";
import { X, Save, AlertTriangle } from "lucide-react";
import type { OCRResult } from "@/types";

interface OCRReviewModalProps {
  isOpen: boolean;
  onClose: () => void;
  ocrData: { file: any; ocrResult: OCRResult } | null;
}

export function OCRReviewModal({ isOpen, onClose, ocrData }: OCRReviewModalProps) {
  const [formData, setFormData] = useState({
    claimantName: "",
    village: "",
    claimId: "",
    area: "",
    surveyNumber: "",
    district: "",
    state: "",
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Update form data when OCR data changes
  useEffect(() => {
    if (ocrData?.ocrResult) {
      const result = ocrData.ocrResult;
      setFormData({
        claimantName: result.claimantName || "",
        village: result.village || "",
        claimId: result.claimId || "",
        area: result.area || "",
        surveyNumber: result.surveyNumber || "",
        district: result.district || "",
        state: result.state || "",
      });
    }
  }, [ocrData]);

  const saveOCRMutation = useMutation({
    mutationFn: api.saveOCRData,
    onSuccess: () => {
      toast({
        title: "Claim Created Successfully",
        description: "OCR data has been reviewed and saved as a new claim.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/claims"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      onClose();
    },
    onError: (error) => {
      toast({
        title: "Save Failed",
        description: error instanceof Error ? error.message : "Failed to save claim data",
        variant: "destructive",
      });
    },
  });

  const handleInputChange = useCallback((field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  }, []);

  const handleSave = useCallback(() => {
    if (!ocrData) return;

    // Validate required fields
    if (!formData.claimantName || !formData.village || !formData.claimId || !formData.area) {
      toast({
        title: "Missing Required Fields",
        description: "Please fill in all required fields: Claimant Name, Village, Claim ID, and Area.",
        variant: "destructive",
      });
      return;
    }

    const saveData = {
      ...formData,
      confidence: ocrData.ocrResult.confidence,
      rawText: ocrData.ocrResult.rawText,
      fileId: ocrData.file.id,
    };

    saveOCRMutation.mutate(saveData);
  }, [formData, ocrData, saveOCRMutation, toast]);

  const handleReject = useCallback(() => {
    toast({
      title: "OCR Data Rejected",
      description: "The extracted data has been rejected and will not be saved.",
    });
    onClose();
  }, [onClose, toast]);

  if (!isOpen || !ocrData) return null;

  const { ocrResult } = ocrData;
  const confidenceColor = 
    ocrResult.confidence >= 80 
      ? "bg-accent/10 text-accent" 
      : ocrResult.confidence >= 60 
      ? "bg-chart-1/10 text-chart-1" 
      : "bg-destructive/10 text-destructive";

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <div className="flex items-center justify-between">
            <DialogTitle className="text-lg font-semibold text-foreground">
              OCR Text Review
            </DialogTitle>
            <Button 
              variant="ghost" 
              size="icon" 
              onClick={onClose}
              data-testid="button-close-modal"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Original Document */}
          <div>
            <h4 className="text-sm font-medium text-foreground mb-3">
              Original Document
            </h4>
            <div className="bg-muted rounded-lg p-4 h-64 flex items-center justify-center">
              <div className="text-center">
                <AlertTriangle className="h-12 w-12 text-muted-foreground mx-auto mb-2" />
                <p className="text-sm text-muted-foreground">
                  Document preview not available
                </p>
                <p className="text-xs text-muted-foreground mt-1">
                  Original file: {ocrData.file.originalName}
                </p>
              </div>
            </div>
          </div>

          {/* Extracted Text */}
          <div>
            <div className="flex items-center justify-between mb-3">
              <h4 className="text-sm font-medium text-foreground">
                Extracted Text
              </h4>
              <Badge className={confidenceColor} data-testid="confidence-badge">
                Confidence: {ocrResult.confidence}%
              </Badge>
            </div>

            <div className="space-y-4">
              <div>
                <Label htmlFor="claimantName" className="text-xs font-medium text-muted-foreground">
                  Claimant Name *
                </Label>
                <Input
                  id="claimantName"
                  value={formData.claimantName}
                  onChange={(e) => handleInputChange("claimantName", e.target.value)}
                  className="mt-1"
                  data-testid="input-claimant-name"
                />
              </div>

              <div>
                <Label htmlFor="village" className="text-xs font-medium text-muted-foreground">
                  Village *
                </Label>
                <Input
                  id="village"
                  value={formData.village}
                  onChange={(e) => handleInputChange("village", e.target.value)}
                  className="mt-1"
                  data-testid="input-village"
                />
              </div>

              <div>
                <Label htmlFor="claimId" className="text-xs font-medium text-muted-foreground">
                  Claim ID *
                </Label>
                <Input
                  id="claimId"
                  value={formData.claimId}
                  onChange={(e) => handleInputChange("claimId", e.target.value)}
                  className="mt-1"
                  data-testid="input-claim-id"
                />
              </div>

              <div>
                <Label htmlFor="area" className="text-xs font-medium text-muted-foreground">
                  Area (Hectares) *
                </Label>
                <Input
                  id="area"
                  type="number"
                  step="0.01"
                  value={formData.area}
                  onChange={(e) => handleInputChange("area", e.target.value)}
                  className="mt-1"
                  data-testid="input-area"
                />
              </div>

              <div>
                <Label htmlFor="surveyNumber" className="text-xs font-medium text-muted-foreground">
                  Survey Number
                </Label>
                <Input
                  id="surveyNumber"
                  value={formData.surveyNumber}
                  onChange={(e) => handleInputChange("surveyNumber", e.target.value)}
                  className="mt-1"
                  data-testid="input-survey-number"
                />
              </div>

              <div className="grid grid-cols-2 gap-2">
                <div>
                  <Label htmlFor="district" className="text-xs font-medium text-muted-foreground">
                    District
                  </Label>
                  <Input
                    id="district"
                    value={formData.district}
                    onChange={(e) => handleInputChange("district", e.target.value)}
                    className="mt-1"
                    data-testid="input-district"
                  />
                </div>

                <div>
                  <Label htmlFor="state" className="text-xs font-medium text-muted-foreground">
                    State
                  </Label>
                  <Input
                    id="state"
                    value={formData.state}
                    onChange={(e) => handleInputChange("state", e.target.value)}
                    className="mt-1"
                    data-testid="input-state"
                  />
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Raw OCR Output */}
        <div className="mt-6">
          <h4 className="text-sm font-medium text-foreground mb-3">
            Raw OCR Text
          </h4>
          <Textarea
            value={ocrResult.rawText}
            readOnly
            className="h-32 font-mono text-xs resize-none"
            data-testid="textarea-raw-ocr"
          />
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end space-x-3 pt-6 border-t border-border">
          <Button 
            variant="outline" 
            onClick={onClose}
            data-testid="button-cancel"
          >
            Cancel
          </Button>
          <Button 
            variant="destructive" 
            onClick={handleReject}
            data-testid="button-reject"
          >
            <X className="mr-2 h-4 w-4" />
            Reject
          </Button>
          <Button 
            onClick={handleSave}
            disabled={saveOCRMutation.isPending}
            data-testid="button-save"
          >
            <Save className="mr-2 h-4 w-4" />
            {saveOCRMutation.isPending ? "Saving..." : "Save & Continue"}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

